angular.module("wrektranet.adminProgramLogCtrl", [])

.controller('adminProgramLogCtrl', [
  '$scope',
  function($scope) {
    var padNumber = function(number) {
      return number < 10 ? '0' + number : number;
    };

    $scope.repeatIntervals = [
      { minutes: 0,   label: "Don't repeat" },
      { minutes: 15,  label: "15 minutes" },
      { minutes: 30,  label: "30 minutes" },
      { minutes: 45,  label: "45 minutes" },
      { minutes: 60,  label: "1 hour" },
      { minutes: 90,  label: "1.5 hours" },
      { minutes: 120, label: "2 hours" },
      { minutes: 180, label: "3 hours" }
    ];

    $scope.resetNewSchedule = function() {
      $scope.newSchedule = {
        sunday:          false,
        monday:          false,
        tuesday:         false,
        wednesday:       false,
        thursday:        false,
        friday:          false,
        saturday:        false,
        start_date:      null,
        expiration_date: null,
        start_time:      null,
        end_time:        null,
        repeat_interval: $scope.repeatIntervals[0].minutes
      };
    }

    $scope.addSchedule = function(event) {
      var schedule = angular.copy($scope.newSchedule);
      event.preventDefault();

      if ($scope.plogForm.startTime.$invalid) {
        return false;
      }

      $scope.program_log_entry.program_log_schedules.push(schedule);
      $scope.resetNewSchedule();
    };

    $scope.deleteSchedule = function(schedule) {
      if (schedule.id) {
        // set destroy flag to true
        schedule.destroy = 1;
      } else {
        // unsaved Schedule, remove it entirely
        $scope.program_log_entry.program_log_schedules.splice($.inArray(schedule, $scope.program_log_entry.program_log_schedules), 1);
      }
    };

    $scope.timeOfDay = function(time) {
      if (_.has(time, 'hour')) {
        return padNumber(time.hour) + ":" + padNumber(time.minute);
      } else {
        return time;
      }
    }

    // set $scope.newSchedule with initial values
    $scope.resetNewSchedule();
  }
]);
"use strict";

angular.module("wrektranet.adminTicketCtrl", [])

// container for list of tickets
.controller('adminTicketListCtrl', [
  '$scope',
  'Restangular',
  function($scope, Restangular) {
    Restangular.setBaseUrl('/admin');

    // update contests for any appropriate tickets
    $scope.$on('updateContests', function(e, contest) {
      $scope.$broadcast('updateContest', contest);

      angular.forEach($scope.tickets, function(ticket) {
        if (ticket.contest.id === contest.id) {
          ticket.contest = contest;
        }
      });
    });

    $scope.addTicket = function() {
      var ticket, restangularTicket;

      ticket = {
        contest_id: $scope.contest.id,
        user_id: $scope.user_id
      };

      restangularTicket = Restangular
        .restangularizeElement(null, ticket, 'staff_tickets');

      restangularTicket
        .post()
        .then(function(newTicket) {
          if (newTicket.id !== false) {
            $scope.tickets.push(newTicket);
            $scope.user_id = null;
            $scope.$emit('updateContests', newTicket.contest);
          }
        });
    };
  }
])

// controller for individual ticket rows
.controller('adminTicketCtrl', [
  '$scope',
  'Restangular',
  function($scope, Restangular) {
    Restangular.setBaseUrl('/admin');

    $scope.isContestFull = function() {
      var limit, total;

      limit = $scope.ticket.contest.staff_ticket_limit;
      total = $scope.ticket.contest.staff_count;
      return (limit - total === 0);
    };

    $scope.openEdit = function() {
      var newName = null;

      newName = prompt("Enter a custom name for this ticket:", $scope.ticket.display_name);

      if (newName !== null) {
        $scope.ticket.display_name = newName;
        $scope.saveName();
      }
    };

    $scope.revertName = function() {
      var restangularTicket = Restangular.
        restangularizeElement(null, $scope.ticket, 'staff_tickets');

      restangularTicket.display_name = null;
      restangularTicket.put();
    };

    $scope.saveName = function() {
      var restangularTicket = Restangular.
        restangularizeElement(null, $scope.ticket, 'staff_tickets');

      restangularTicket.put();
    };

    $scope.award = function() {
      var restangularTicket = Restangular.
        restangularizeElement(null, $scope.ticket, 'staff_tickets');

      restangularTicket.awarded = true;
      restangularTicket
        .put()
        .then(function(updatedTicket) {
          $scope.ticket.awarded = true;
          $scope.$emit('updateContests', updatedTicket.contest);
        });
    };

    $scope.unaward = function() {
      var restangularTicket = Restangular
        .restangularizeElement(null, $scope.ticket, 'staff_tickets');

      restangularTicket.awarded = false;
      restangularTicket
        .put()
        .then(function(updatedTicket) {
          $scope.ticket.awarded = false;
          $scope.$emit('updateContests', updatedTicket.contest);
        });
    };

    $scope.remove = function() {
      var restangularTicket = Restangular
        .restangularizeElement(null, $scope.ticket, 'staff_tickets');

      if (confirm("Are you sure you want to delete this signup?")) {
        restangularTicket
          .remove()
          .then(function() {
            _.remove($scope.tickets, $scope.ticket);
            $scope.ticket = null;
          });
      }
    };
  }
]);
"use strict";

angular.module("wrektranet.adminVenueCtrl", [])

.controller('adminVenueCtrl', [
  '$scope',
  function($scope) {
    $scope.newContact = {
      email: ""
    };

    $scope.addContact = function(event) {
      var contact = angular.copy($scope.newContact);
      event.preventDefault();

      if (contact.email.length === 0 || $scope.venueForm.newContactEmail.$invalid) {
        return false;
      }

      $scope.venue.contacts.push(contact);
      $scope.newContact.email = "";
    };

    $scope.deleteContact = function(contact) {
      if (contact.id) {
        // set destroy flag to true
        contact.destroy = 1;
      } else {
        // unsaved contact, remove it entirely
        $scope.venue.contacts.splice($.inArray(contact, $scope.venue.contacts), 1);
      }
    }
  }
]);
